import React, { useState, useEffect } from 'react';
import { getTopArtists, getArtistDetails } from '../api';

const TopArtists = () => {
  const [artists, setArtists] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [currentSong, setCurrentSong] = useState(null);

  useEffect(() => {
    const fetchArtists = async () => {
      const data = await getTopArtists();
      setArtists(data);
    };

    fetchArtists();
  }, []);

  const handleArtistClick = async (artistId) => {
    const artistDetails = await getArtistDetails(artistId);
    setSelectedArtist(artistDetails);
  };

  const handleSongClick = (songUrl) => {
    setCurrentSong(songUrl);
  };

  return (
    <div>
      <h2>Top Artists</h2>
      <ul>
        {artists.map(artist => (
          <li key={artist.id} onClick={() => handleArtistClick(artist.id)}>
            {artist.name}
          </li>
        ))}
      </ul>
      {selectedArtist && (
        <div>
          <h3>{selectedArtist.name}</h3>
          <img src={selectedArtist.image} alt={selectedArtist.name} />
          <h4>Songs</h4>
          <ul>
            {selectedArtist.songs.map(song => (
              <li key={song.id} onClick={() => handleSongClick(song.url)}>
                {song.name}
              </li>
            ))}
          </ul>
        </div>
      )}
      {currentSong && (
        <audio controls autoPlay>
          <source src={currentSong} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>
      )}
    </div>
  );
};

export default TopArtists;
