import React, { useState } from 'react';
import styled from 'styled-components';
import TopArtists from './TopArtists';

const HomeContainer = styled.div`
  padding: 20px;
  text-align: center;
`;

const SectionTitle = styled.h2`
  margin-top: 40px;
  margin-bottom: 20px;
`;

const SectionContainer = styled.div`
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
`;

const Card = styled.div`
  width: 200px;
  padding: 20px;
  margin: 10px;
  background-color: #1c1c1c;
  color: white;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  transition: transform 0.2s;

  &:hover {
    transform: scale(1.05);
  }
`;

const SearchContainer = styled.div`
  display: flex;
  justify-content: center;
  margin: 20px 0;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 300px;
  border: none;
  border-radius: 5px;
  font-size: 16px;
`;

const ResultCard = styled(Card)`
  display: flex;
  align-items: center;
  padding: 10px;
  margin: 10px;
`;

const ResultImage = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  margin-right: 10px;
`;

const SongImage = styled.img`
  width: 100%;
  height: 150px;
  object-fit: cover;
  border-radius: 8px 8px 0 0;
`;

const Home = ({ songs, onSelectSong }) => {
  const [searchResults, setSearchResults] = useState([]);
  const [artistSongs, setArtistSongs] = useState([]);

  const handleSearch = (query) => {
    if (query === '') {
      setSearchResults([]);
      return;
    }

    const songResults = songs.filter(song =>
      (song.name && song.name.toLowerCase().includes(query.toLowerCase())) ||
      (song.artist && song.artist.toLowerCase().includes(query.toLowerCase()))
    );

    setSearchResults(songResults);
  };

  return (
    <HomeContainer>
      <h1>Welcome to Spotify 2.0</h1>

      <SearchContainer>
        <SearchInput
          type="text"
          placeholder="Search for songs..."
          onChange={(e) => handleSearch(e.target.value)}
        />
      </SearchContainer>

      {searchResults.length > 0 && (
        <div>
          <h3>Search Results</h3>
          <SectionContainer>
            {searchResults.map((song, index) => (
              <ResultCard key={index} onClick={() => onSelectSong(song, index)}>
                <ResultImage src={song.image} alt={song.name} />
                <div>
                  <h4>{song.name}</h4>
                  <p>by {song.artist}</p>
                </div>
              </ResultCard>
            ))}
          </SectionContainer>
        </div>
      )}

      <TopArtists />

      <SectionTitle>Top Songs</SectionTitle>
      <SectionContainer>
        {songs.slice(0, 4).map((song, index) => (
          <Card key={song.id} onClick={() => onSelectSong(song, index)}>
            <SongImage src={song.image} alt={song.name} />
            <h3>{song.name}</h3>
            <p>by {song.artist}</p>
          </Card>
        ))}
      </SectionContainer>
    </HomeContainer>
  );
};

export default Home;
