import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import SearchBar from '../components/SearchBar';  // Assuming you have a SearchBar component

const HomeContainer = styled.div`
  padding: 20px;
  text-align: center;
`;

const SectionTitle = styled.h2`
  margin-top: 40px;
  margin-bottom: 20px;
`;

const SectionContainer = styled.div`
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
`;

const Card = styled.div`
  width: 300px;
  padding: 20px;
  margin: 10px;
  background-color: #1c1c1c;
  color: white;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  cursor: pointer;
`;

const SearchContainer = styled.div`
  display: flex;
  justify-content: center;
  margin: 20px 0;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 300px;
  border: none;
  border-radius: 5px;
  font-size: 16px;
`;

const Home = ({ songs }) => {
  const navigate = useNavigate();
  const artists = [
    { id: 1, name: 'JIN', image: 'http://localhost:5000/images/jin.jpg' },
    { id: 2, name: 'Chris Brown', image: 'http://localhost:5000/images/chris-brown.jpg' },
    { id: 3, name: 'David Guetta', image: 'http://localhost:5000/images/david-guetta.jpg' }
  ];

  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (query) => {
    const results = songs.filter(song => {
      // Check if song.name and song.artist are defined and not null before accessing
      return (
        song.name && typeof song.name === 'string' &&
        song.artist && typeof song.artist === 'string' &&
        (song.name.toLowerCase().includes(query.toLowerCase()) ||
        song.artist.toLowerCase().includes(query.toLowerCase()))
      );
    });
    setSearchResults(results);
  };

  const handleArtistClick = (artistName) => {
    navigate(`/artist/${artistName}`);
  };

  return (
    <HomeContainer>
      <h1>Welcome to Spotify 2.0</h1>

      <SearchContainer>
        <SearchInput
          type="text"
          placeholder="Search for songs, artists..."
          onChange={(e) => handleSearch(e.target.value)}
        />
      </SearchContainer>
      {searchResults && searchResults.length > 0 && (
        <div>
          <h3>Search Results</h3>
          <ul>
            {searchResults.map((result, index) => (
              <li key={index}>
                {result.name} by {result.artist}
              </li>
            ))}
          </ul>
        </div>
      )}

      <SectionTitle>Top Artists</SectionTitle>
      <SectionContainer>
        {artists.map(artist => (
          <Card key={artist.id} onClick={() => handleArtistClick(artist.name)}>
            <img src={artist.image} alt={artist.name} style={{ width: '100%', borderRadius: '50%' }} />
            <h3>{artist.name}</h3>
          </Card>
        ))}
      </SectionContainer>

      {/* Placeholder for Top Songs or other sections */}
    </HomeContainer>
  );
};

export default Home;
