import React, { useState } from 'react';
import styled from 'styled-components';
import SearchBar from '../components/SearchBar';
const HomeContainer = styled.div`
  padding: 20px;
  text-align: center;
`;

const SectionTitle = styled.h2`
  margin-top: 40px;
  margin-bottom: 20px;
`;

const SectionContainer = styled.div`
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
`;

const Card = styled.div`
  width: 300px;
  padding: 20px;
  margin: 10px;
  background-color: #1c1c1c;
  color: white;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const Home = () => {
  // Dummy data for demonstration
  const artists = [
    { id: 1, name: 'JIN', image: 'http://localhost:5000/images/jin.jpg' },
    { id: 2, name: 'Chris Brown', image: 'http://localhost:5000/images/chris-brown.jpg' },
    { id: 3, name: 'David Guetta', image: 'http://localhost:5000/images/david-guetta.jpg' }
  ];

  const topSongs = [
    { id: 1, name: 'Song 1', artist: 'Artist 1' },
    { id: 2, name: 'Song 2', artist: 'Artist 2' },
    { id: 3, name: 'Song 3', artist: 'Artist 3' }
  ];

  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (query) => {
    // Simulate search results based on the query
    const results = topSongs.filter(song =>
      song.name.toLowerCase().includes(query.toLowerCase()) ||
      song.artist.toLowerCase().includes(query.toLowerCase())
    );
    setSearchResults(results);
  };

  return (
    <HomeContainer>
      <h1>Welcome to Spotify 2.0</h1>

      {/* SearchBar component with onSearch prop */}
      <SearchBar onSearch={handleSearch} searchResults={searchResults} />

      <SectionTitle>Top Artists</SectionTitle>
      <SectionContainer>
        {artists.map(artist => (
          <Card key={artist.id}>
            <img src={artist.image} alt={artist.name} style={{ width: '100%', borderRadius: '50%' }} />
            <h3>{artist.name}</h3>
          </Card>
        ))}
      </SectionContainer>

      <SectionTitle>Top Songs</SectionTitle>
      <SectionContainer>
        {topSongs.map(song => (
          <Card key={song.id}>
            <h3>{song.name}</h3>
            <p>by {song.artist}</p>
          </Card>
        ))}
      </SectionContainer>

    </HomeContainer>
  );
};

export default Home;
