import React, { useState } from 'react';
import styled from 'styled-components';
import SongList from './SongList';

const HomeContainer = styled.div`
  padding: 20px;
  text-align: center;
`;

const SearchContainer = styled.div`
  display: flex;
  justify-content: center;
  margin: 20px 0;
`;

const SearchInput = styled.input`
  padding: 10px;
  width: 300px;
  border: none;
  border-radius: 5px;
  font-size: 16px;
`;

const Home = ({ songs, onSelectSong }) => {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (query) => {
    const songResults = songs.filter(song =>
      (song.title && song.title.toLowerCase().includes(query.toLowerCase())) ||
      (song.artist && song.artist.toLowerCase().includes(query.toLowerCase()))
    );

    setSearchResults(songResults);
  };

  return (
    <HomeContainer>
      <h1>Welcome to Spotify 2.0</h1>

      <SearchContainer>
        <SearchInput
          type="text"
          placeholder="Search for songs, artists..."
          onChange={(e) => handleSearch(e.target.value)}
        />
      </SearchContainer>

      {searchResults.length > 0 ? (
        <SongList songs={searchResults} onSelectSong={onSelectSong} />
      ) : (
        <>
          <h2>Top Songs</h2>
          <SongList songs={songs} onSelectSong={onSelectSong} />
        </>
      )}
    </HomeContainer>
  );
};

export default Home;
