import React, { useState, useEffect } from 'react';
import { getSongs } from '../api';
import ReactAudioPlayer from 'react-audio-player';
import styled from 'styled-components';

const SongContainer = styled.div`
  padding: 20px;
`;

const SongListContainer = styled.ul`
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
`;

const SongItem = styled.li`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 150px;
  cursor: pointer;
  transition: transform 0.2s;
  &:hover {
    transform: scale(1.05);
  }
`;

const SongImage = styled.img`
  width: 100px;
  height: 100px;
  border-radius: 10px;
`;

const SongTitle = styled.span`
  margin-top: 10px;
  font-size: 16px;
  text-align: center;
`;

const NowPlaying = styled.div`
  margin-top: 20px;
  text-align: center;
`;

const SongList = () => {
  const [songs, setSongs] = useState([]);
  const [currentSong, setCurrentSong] = useState(null);

  useEffect(() => {
    const fetchSongs = async () => {
      const data = await getSongs();
      setSongs(data);
    };

    fetchSongs();
  }, []);

  return (
    <SongContainer>
      <h2>Songs</h2>
      <SongListContainer>
        {songs.map(song => (
          <SongItem key={song.id} onClick={() => setCurrentSong(song)}>
            <SongImage src={song.image} alt={song.title} />
            <SongTitle>{song.title} by {song.artist}</SongTitle>
          </SongItem>
        ))}
      </SongListContainer>
      {currentSong && (
        <NowPlaying>
          <h3>Now Playing: {currentSong.title} by {currentSong.artist}</h3>
          <ReactAudioPlayer
            src={currentSong.url}
            controls
            autoPlay
          />
        </NowPlaying>
      )}
    </SongContainer>
  );
};

export default SongList;
